angular.module('app', ['ngRoute', 'ngAnimate', 'appRoutes', 'app.factories', 'MainCtrl', 'HomeCtrl', 'PortfolioCtrl']);
angular.module('appRoutes', []).config(['$routeProvider', '$locationProvider', function($routeProvider, $locationProvider) {

    $routeProvider

        .when('/', {
            templateUrl: '/templates/home/home.view.html',
            controller: 'HomeController'
        })

        .when('/home', {
            redirectTo: '/'
        })

        .when('/portfolio', {
            templateUrl: '/templates/portfolio/portfolio.view.html',
            controller: 'PortfolioController'
        });

        // .otherwise({
        //     templateUrl:'errors/404.view.jade'
        // });


    $locationProvider.html5Mode({
        enabled: true,
        requireBase: false
    });

}]);
angular.module('HomeCtrl', []).controller('HomeController', function($scope, Page) {

   Page.setTitle('Home');   

});
angular.module('PortfolioCtrl', []).controller('PortfolioController', function($scope, $http, Page) {

  Page.setTitle('Portfolio');  

  $http.get('api/portfolio')
    .success(function (res) {
      $scope.portfolioList = res;
    });

  $scope.$on('$routeChangeSuccess', function (event, data) {
    $scope.errormessage = '';
    $scope.pageTitle = data.title + ' | MEAN Boilerplate by Matt Sloan';
  });
});

angular.module('MainCtrl', []).controller('MainController', function($scope, $http, Page) {

  $scope.Page = Page;
  
  $scope.tagline = 'main';  

  $http.get('api/navigation')
    .success(function (res) {
      $scope.navigation = res
    });

  $scope.$on('$routeChangeSuccess', function (event, data) {
    $scope.errormessage = '';
    $scope.pageTitle = data.title + ' | MEAN Boilerplate by Matt Sloan';
  });

});
