angular.module('sloanApp', ['ngRoute', 'appRoutes', 'app.factories', 'MainCtrl', 'HomeCtrl', 'PortfolioCtrl']);
angular.module('appRoutes', []).config(['$routeProvider', '$locationProvider', function($routeProvider, $locationProvider) {

    $routeProvider

        .when('/', {
            templateUrl: '/templates/home/home.view.html',
            controller: 'HomeController'
        })

        .when('/home', {
            redirectTo: '/'
        })

        .when('/portfolio', {
            templateUrl: '/templates/portfolio/portfolio.view.html',
            controller: 'PortfolioController'
        });

        // .otherwise({
        //     templateUrl:'errors/404.view.jade'
        // });


    $locationProvider.html5Mode({
        enabled: true,
        requireBase: false
    });

}]);
angular.module('HomeCtrl', []).controller('HomeController', function($scope) {

   $scope.title = 'Home';   

});
angular.module('PortfolioCtrl', []).controller('PortfolioController', function($scope) {

   $scope.title = 'Portfolio';   

});

angular.module('MainCtrl', []).controller('MainController', function($scope, $http) {

   $scope.tagline = 'main';  

   $http.get('api/navigation')
    .success(function (res) {
      $scope.navigation = res
    });

});
